import { Image, Button, Space } from 'antd';
import { ReloadOutlined, CameraOutlined, VideoCameraAddOutlined, VideoCameraOutlined } from '@ant-design/icons';
import React, { useEffect, useState } from 'react';
import openNotificationWithIcon from '../../utils/notification';

const USMVideo = ({ipCamera, loadings, enterLoading}) => {

  const handleReconect = () => {
    document.getElementById("browser-video").firstChild.src = "http://" + ipCamera + ":8080/video"
  }

  useEffect(() => {
    document.getElementById("browser-video").firstChild.src = "http://" + ipCamera + ":8080/video"
  }, [ipCamera])

  const handleErrorVideo = () => {
    document.getElementById("browser-video").firstChild.src = "data:image/png;base64,"
  }

  const getBase64FromUrl = async (url) => {
    const data = await fetch(url);
    const blob = await data.blob();
    return new Promise((resolve) => {
      const reader = new FileReader();
      reader.readAsDataURL(blob); 
      reader.onloadend = () => {
        const base64data = reader.result;   
        resolve(base64data);
      }
    });
  }

  const handleShotImage = () => {
    getBase64FromUrl("http://" + ipCamera + ":8080/photo.jpg")
      .then(res => {
        document.getElementById("browser-image").firstChild.src = res
        window.localStorage.setItem("USM_TEMP_IMAGE", res)
      })
      .catch(e => {
        openNotificationWithIcon(
          'error',
          'Chụp không thành công',
          'Lỗi có thể do mất kết nối với Camera, hãy kết nối lại!'
        )
      })
  }
  
  return (
    <div
      style={{
        position: "relative",
        width: "48%",
        margin: 10,
        backgroundColor: "",
        minHeight: 300,
        minWidth: 300,
        display: "flex",
        alignItems: "center",
        flexDirection: "column",
        justifyContent: "space-around",
        borderRadius: "10px",
        boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
      }}
    >
      <Space
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
          alignItems: "center",
          width: "40%",
          height: "40px",
          margin: "0",
          padding: "0",
          color: "rgba(0,0,0,.85)",
          fontSize: "14px",
          fontVariant: "tabular-nums",
          lineHeight: "1.5715",
          listStyle: "none",
          fontFeatureSettings: "tnum",
          position: "relative",
          background: "#fff",
          borderRadius: "10px",
        }}
      >
        <VideoCameraOutlined /> 
        <span>Màn hình trực tuyến</span>
      </Space>
      <Image
          preview={false} 
          style={{
            position: "relative",
            padding: 10,
            width: "100%",
            minHeight: "300px",
            borderRadius: "10px",
            boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
          }}
          id="browser-video" src={"http://" + ipCamera + ":8080/video"}
          onError={() => handleErrorVideo()}
        />
      <Space
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
          margin: 10,
          width: "80%"
        }}
      >
        <Button type="primary" icon={<ReloadOutlined />} shape="round" size="large" block loading={loadings[0]} 
          onClick={() => {
            handleReconect()
            enterLoading(0, 1000)
          }}
          style={{
            boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
          }}
        
        >
          Kết nối lại
        </Button>
        <Button type="primary" shape="round" size="large" block loading={loadings[1]} 
          icon={<CameraOutlined />}
          onClick={() => {
            handleShotImage()
            enterLoading(1, 500)
          }}
          style={{
            boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
          }}  
        >
          Chụp ảnh
        </Button>
      </Space>
    </div>
  )
}

const USMImage = ({loadings, enterLoading, setImages}) => {

  const handleErrorShot = () => {
    document.getElementById("browser-image").firstChild.src = "data:image/png;base64,"
  }

  const handleAdd = () => {
    const image = window.localStorage.getItem("USM_TEMP_IMAGE")
    if (image) {
      setImages(images => [...images, image])
    }
  }

  return (
    <div
      style={{
        position: "relative",
        width: "48%",
        margin: 10,
        backgroundColor: "",
        minHeight: 300,
        minWidth: 300,
        display: "flex",
        alignItems: "center",
        flexDirection: "column",
        justifyContent: "space-around",
        borderRadius: "10px",
        boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
      }}
    >
      <Space
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
          alignItems: "center",
          width: "40%",
          height: "40px",
          margin: "0",
          padding: "0",
          color: "rgba(0,0,0,.85)",
          fontSize: "14px",
          fontVariant: "tabular-nums",
          lineHeight: "1.5715",
          listStyle: "none",
          fontFeatureSettings: "tnum",
          position: "relative",
          background: "#fff",
          borderRadius: "10px",
        }}
      >
        <VideoCameraOutlined /> 
        <span>Ảnh chụp từ điện thoại</span>
      </Space>
      <Image
        preview={false}
        style={{
          position: "relative",
          padding: 10,
          width: "100%",
          minHeight: "300px",
          borderRadius: "10px",
          boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
        }}
        id="browser-image" src="error"
        onError={() => handleErrorShot()}
      />
      <Space
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-around",
          margin: 10,
          width: "80%"
        }}
      >
        <Button type="danger" shape="round" size="large" icon={<VideoCameraAddOutlined />} 
          block loading={loadings[2]} 
          onClick={() => {
            handleAdd()
            enterLoading(2, 300)
          }} 
          style={{
            boxShadow: "0 1px 2px -2px rgb(0 0 0 / 16%), 0 3px 6px 0 rgb(0 0 0 / 12%), 0 5px 12px 4px rgb(0 0 0 / 9%)",
          }}
        >
          Chọn ảnh này
        </Button>
      </Space>
    </div>
  )
}

const USMCamera = () => {
  // eslint-disable-next-line 
  const [ipCamera, setIpCamera] = useState('0.0.0.0')
  const [images, setImages] = useState([])
  const [loadings, setLoadings] = useState([]);

  const enterLoading = (index, timeout) => {
    setLoadings((prevLoadings) => {
      const newLoadings = [...prevLoadings];
      newLoadings[index] = true;
      return newLoadings;
    });
    setTimeout(() => {
      setLoadings((prevLoadings) => {
        const newLoadings = [...prevLoadings];
        newLoadings[index] = false;
        return newLoadings;
      });
    }, timeout);
  };

  return (
    <div style={{
      display: "flex",
      flexDirection: "row",
      justifyContent: "space-between",
      flexWrap: "wrap"
    }}>
      <USMVideo ipCamera={ipCamera} loadings={loadings} enterLoading={enterLoading}/>
      <USMImage loadings={loadings} enterLoading={enterLoading} setImages={setImages}/>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          flexWrap: "wrap",
          justifyContent: "flex-start",
        }}
      >
        {images.map((image, id) => {
          return (<Image
            key={id}
            width={200}
            style={{
              padding: "10px",
            }}
            src={image}
          />)
        })}
      </div>
    </div>
  );
}

export default USMCamera;